import React, { useState, useEffect, useContext } from 'react';
import { Redirect } from 'react-router-dom';
import { GoogleLogin } from 'react-google-login';
import AppContext from './libs/contextLib';

const LoginPage = () => {
    const { isAuthenticated, setIsAuthenticated, setUserData } = useContext(AppContext);
    const handleLogin = async googleData => {

        const res = await fetch("/test/g-auth.php", {
            method: "POST",
            body: JSON.stringify({
                token: googleData.tokenId
            }),
            headers: {
                "Content-Type": "application/json"
            }
        })
        const Logindata = await res.json();

        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ func: 'register_login_user', data: Logindata })
        };
        fetch("/test/quiz_apis/post_requests.php", requestOptions).then(response => response.json())
            .then(data => {
                localStorage.setItem('userdata', JSON.stringify(data.data));
                setUserData(data.data);
                setIsAuthenticated(true);
            });
        // store returned user somehow
    }

    return (
        <>
            {isAuthenticated ? <Redirect to="" /> : <GoogleLogin
                clientId="293677709302-icb6g950tk8q3vvia8i692f4ogu7l88p.apps.googleusercontent.com"
                buttonText="Log in with Google"
                onSuccess={handleLogin}
                onFailure={handleLogin}
                cookiePolicy={'single_host_origin'}
            />}
        </>

    );
}

export default LoginPage;
