import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button, Container } from 'react-bootstrap';

import AppContext from './libs/contextLib';
import { Switch, BrowserRouter as Router, Redirect, useParams, Route } from 'react-router-dom';
import LoginPage from './LoginPage';
import Navmenu from './NavMenu';
export default function App() {
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [isloading, setIsLoading] = useState(true);
    const [userData, setUserData] = useState({});

    const handleLogout = () => {
        setIsAuthenticated(false);
        localStorage.setItem('userdata', '');
    }

    const Home = () => {
        return (
            <>

                <Container>
                    <div className='row bg-light p-5 my-5 rounded'>
                        <div className='col-md-12 text-center'>
                            {isAuthenticated ? (
                                <p>Welcome {userData.name} </p>
                            ) : (
                                    <>Please Login</>
                                )}
                        </div>
                    </div>
                </Container>
            </>
        );
    }

    const NotFound = () => {
        return (
            <Route render={({ staticContext }) => {
                if (staticContext) {
                    staticContext.status = 404;
                }
                return (
                    <div>
                        <h1>404 : Not Found</h1>
                    </div>
                )
            }} />
        );
    };

    const QuizMain = () => {
        const questions = [
            {
                questionText: 'What is the capital of France?',
                answerOptions: [
                    { answerText: 'New York', isCorrect: false },
                    { answerText: 'London', isCorrect: false },
                    { answerText: 'Paris', isCorrect: true },
                    { answerText: 'Dublin', isCorrect: false },
                ],
            },
            {
                questionText: 'Who is CEO of Tesla?',
                answerOptions: [
                    { answerText: 'Jeff Bezos', isCorrect: false },
                    { answerText: 'Elon Musk', isCorrect: true },
                    { answerText: 'Bill Gates', isCorrect: false },
                    { answerText: 'Tony Stark', isCorrect: false },
                ],
            },
            {
                questionText: 'The iPhone was created by which company?',
                answerOptions: [
                    { answerText: 'Apple', isCorrect: true },
                    { answerText: 'Intel', isCorrect: false },
                    { answerText: 'Amazon', isCorrect: false },
                    { answerText: 'Microsoft', isCorrect: false },
                ],
            },
            {
                questionText: 'How many Harry Potter books are there?',
                answerOptions: [
                    { answerText: '1', isCorrect: false },
                    { answerText: '4', isCorrect: false },
                    { answerText: '6', isCorrect: false },
                    { answerText: '7', isCorrect: true },
                ],
            },
        ];
        return (
            <></>
        );
    }
    return (
        <AppContext.Provider value={{ isAuthenticated, setIsAuthenticated, userData, setUserData }}>
            <Navmenu handleLogout={handleLogout} />
            <Container className="containerMain">
                <Router>
                    <Switch>
                        <Route exact path="/" component={Home} />
                        <Route exact path="/login" component={LoginPage} />
                        <Route path="/dw-video/:video_url" children={<QuizMain />} />
                        <Route component={NotFound} />
                    </Switch>
                </Router>
            </Container>
        </AppContext.Provider>
    );
}
