import React, { useContext } from 'react';
import AppContext from './libs/contextLib';
import LoginPage from './LoginPage';
const QuizMain = () => {
    const questions = [
        {
            questionText: 'What is the capital of France?',
            answerOptions: [
                { answerText: 'New York', isCorrect: false },
                { answerText: 'London', isCorrect: false },
                { answerText: 'Paris', isCorrect: true },
                { answerText: 'Dublin', isCorrect: false },
            ],
        },
        {
            questionText: 'Who is CEO of Tesla?',
            answerOptions: [
                { answerText: 'Jeff Bezos', isCorrect: false },
                { answerText: 'Elon Musk', isCorrect: true },
                { answerText: 'Bill Gates', isCorrect: false },
                { answerText: 'Tony Stark', isCorrect: false },
            ],
        },
        {
            questionText: 'The iPhone was created by which company?',
            answerOptions: [
                { answerText: 'Apple', isCorrect: true },
                { answerText: 'Intel', isCorrect: false },
                { answerText: 'Amazon', isCorrect: false },
                { answerText: 'Microsoft', isCorrect: false },
            ],
        },
        {
            questionText: 'How many Harry Potter books are there?',
            answerOptions: [
                { answerText: '1', isCorrect: false },
                { answerText: '4', isCorrect: false },
                { answerText: '6', isCorrect: false },
                { answerText: '7', isCorrect: true },
            ],
        },
    ];
    const { video_url } = useParams();
    const { isAuthenticated, userData } = useContext(AppContext);
    const [currentQuestion, setCurrentQuestion] = useState(0);
    const [showScore, setShowScore] = useState(false);
    const [score, setScore] = useState(0);
    const [totalScore, setTotalScore] = useState(0);
    const [participated, setParticipated] = useState(0);
    const [answered, setAnswered] = useState([]);
    const [clicked, setClicked] = useState([]);
    const [answeredCount, setAnsweredCount] = useState(0);
    const currentVideo = video_url;
    const [isloading, setIsLoading] = useState(true);
    useEffect(() => {
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                func: 'getThisVideoStatus', data: {
                    user_id: userDataL.user_id,
                    video_id: video_url
                }
            })
        };
        fetch("/test/quiz_apis/post_requests.php", requestOptions).then(response => response.json())
            .then(d => {
                setIsLoading(false);
                if (d.error == -1) {
                    history.push("/");
                } else {
                    if (d.error == 0) {
                        let answeredCountLocal = d.data.answer_count;
                        let scoreCountLocal = d.data.score_count;

                        if (scoreCountLocal !== "") {
                            setScore(parseInt(scoreCountLocal));
                        }
                        if (answeredCountLocal !== "") {
                            setAnsweredCount(parseInt(answeredCountLocal));
                            if (answeredCountLocal < questions.length) {
                                setCurrentQuestion(parseInt(answeredCountLocal));
                            } else {
                                setShowScore(true);
                            }
                        }
                    } else {
                        alert("Something Went wrong, please try again!");
                    }
                }
            });
        const requestOptionsTotal = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                func: 'getTotalScore', data: {
                    user_id: userDataL.user_id
                }
            })
        };
        fetch("/test/quiz_apis/post_requests.php", requestOptionsTotal).then(response => response.json())
            .then(d => {
                if (!d.error) {
                    setTotalScore(d.data.score_count);
                    setParticipated(d.data.participated);
                }
            });
    });
    return (
        <>
            {!isAuthenticated ? <LoginPage /> : <>Hii</>}
        </>
    );
}
export default QuizMain;
